package com.coRamirez.principal.implementaciones;
import com.coRamirez.principal.UNO.*;


public class Sumar implements OperacionInterace{

	@Override
	public double sumar(double a, double b) {
		// TODO Auto-generated method stub
		return a+b;
	}

	@Override
	public double restar(double a, double b) {
		// TODO Auto-generated method stub
		return a-b;
	}

	@Override
	public double mulriplicar(double a, double b) {
		// TODO Auto-generated method stub
		return a*b;
	}

	@Override
	public double dividir(double a, double b) {
		// TODO Auto-generated method stub
		return a/b;
	}

	

}
